import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Plot from 'react-plotly.js';
import { Table, Alert } from 'react-bootstrap';
import { useLocation } from 'react-router-dom';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function ScatterPlot({ draftId, scatterDataLoaded, r2Data }) {
  const query = useQuery();
  const [scatterData, setScatterData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchScatterData = async () => {
      const draftId = query.get('draft_id');
      if (!draftId) {
        console.error("Draft ID is required");
        setError("Draft ID is required");
        return;
      }
      try {
        const response = await axios.get(`http://localhost:5050/scatter_data?draft_id=${draftId}`);
        setScatterData(response.data);
      } catch (error) {
        console.error('Error fetching scatter data:', error);
        setError('Error fetching scatter data');
      }
    };

    if (!scatterDataLoaded) {
      fetchScatterData();
    }
  }, [draftId, scatterDataLoaded]);

  if (error) return <Alert variant="danger">{error}</Alert>;
  if (!scatterData) return <div>Loading...</div>;

  const trace = {
    x: scatterData.scatterplot.pick_no,
    y: scatterData.scatterplot.metadata_amount,
    mode: 'markers',
    marker: {
      color: scatterData.scatterplot.colors,
      size: 10
    },
    text: scatterData.scatterplot.player_names
  };
  const layout = {
    xaxis: {
      title: 'Pick Number'
    },
    yaxis: {
      title: 'Amount'
    }
  };

  return (
    <div>
      <Plot data={[trace]} layout={layout} />
      <h2>R^2 Values by Position</h2>
      <Table>
        <thead>
          <tr>
            <th>Position</th>
            <th>R^2 Value</th>
            <th>Cost of Waiting (1 Pick)</th>
            <th>Cost of Waiting (5 Picks)</th>
            <th>Cost of Waiting (10 Picks)</th>
            <th>Cost of Waiting (20 Picks)</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(r2Data).map((position) => (
            <tr key={position}>
              <td>{position}</td>
              <td>{r2Data[position].r2.toFixed(2)}</td>
              <td>{r2Data[position].cost_of_waiting['1_pick'].toFixed(2)}</td>
              <td>{r2Data[position].cost_of_waiting['5_picks'].toFixed(2)}</td>
              <td>{r2Data[position].cost_of_waiting['10_picks'].toFixed(2)}</td>
              <td>{r2Data[position].cost_of_waiting['20_picks'].toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default ScatterPlot;
