import React, { useState, useEffect } from 'react';
import Plotly from 'plotly.js-dist';

function App() {
    const [scatterDataLoaded, setScatterDataLoaded] = useState(false);
    const [inflationData, setInflationData] = useState(null);
    const [r2Data, setR2Data] = useState(null);
    const [liveUpdate, setLiveUpdate] = useState(false);
    const [draftId, setDraftId] = useState("");

    useEffect(() => {
        if (liveUpdate) {
            const interval = setInterval(() => {
                fetchInflationData();
                fetchScatterData();
            }, 2000);
            return () => clearInterval(interval);
        }
    }, [liveUpdate]);

    async function fetchInflationData() {
        try {
            const response = await fetch('/inflation', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Accept': 'application/json',
                    'X-Requested-With': 'XMLHttpRequest'
                },
                body: `draft_id=${draftId}&csrf_token=${document.querySelector('meta[name="csrf-token"]').getAttribute('content')}`
            });
            const data = await response.json();
            setInflationData(data);
        } catch (error) {
            console.error('Error fetching inflation data:', error);
        }
    }

    async function fetchScatterData() {
        try {
            const response = await fetch(`/scatter_data?draft_id=${draftId}`);
            const data = await response.json();
            if (data && data.scatterplot) {
                const trace = {
                    x: data.scatterplot.pick_no,
                    y: data.scatterplot.metadata_amount,
                    mode: 'markers',
                    marker: {
                        color: data.scatterplot.colors,
                        size: 10
                    },
                    text: data.scatterplot.player_names
                };
                const layout = {
                    xaxis: { title: 'Pick Number' },
                    yaxis: { title: 'Amount' }
                };
                Plotly.react('scatterPlot', [trace], layout);
                setScatterDataLoaded(true);
                setR2Data(data.r2_values); // Store R^2 data
            }
        } catch (error) {
            console.error('Error fetching scatter data:', error);
        }
    }

    const handleToggleSwitch = () => {
        setLiveUpdate(!liveUpdate);
    };

    const handleDraftIdChange = (e) => {
        setDraftId(e.target.value);
    };

    return (
        <div>
            <h1>Control Board</h1>
            <div style={{ margin: '1rem 0' }}>
                <label>Live Update:</label>
                <input id="liveUpdateSwitch" onChange={handleToggleSwitch} type="checkbox" />
            </div>
            <form>
                <input type="hidden" name="csrf_token" value={document.querySelector('meta[name="csrf-token"]').getAttribute('content')} />
                <label htmlFor="draft_id">Enter Draft ID:</label>
                <input id="draft_id" name="draft_id" type="text" value={draftId} onChange={handleDraftIdChange} />
            </form>
            <button onClick={fetchScatterData}>Load Data</button>

            <div className="tab">
                <button className="tablinks" onClick={() => setScatterDataLoaded(false)}>Inflation Data</button>
                <button className="tablinks" onClick={fetchScatterData}>Picks Over Time</button>
            </div>

            {inflationData && (
                <div id="InflationData">
                    <h2>Overall Inflation</h2>
                    <p id="overall-inflation-display">Overall Inflation: {inflationData.overall_inflation}</p>
                    <h2>Positional Inflation</h2>
                    {/* Render positional inflation table */}
                    {Object.entries(inflationData.positional_inflation).map(([position, inflation]) => (
                        <div key={position}>
                            <h3>{position}</h3>
                            <p>{inflation}</p>
                        </div>
                    ))}
                </div>
            )}

            <div id="ScatterPlot" style={{ display: scatterDataLoaded ? 'block' : 'none' }}>
                <h2>Draft Plot</h2>
                <div id="scatterPlot" style={{ height: '600px' }}></div>
                <button onClick={fetchScatterData}>Load Data</button>
            </div>

            {r2Data && (
                <div id="R2Data">
                    <h2>R^2 Values</h2>
                    {/* Render R^2 table */}
                    <table>
                        <thead>
                            <tr>
                                <th>Position</th>
                                <th>R^2</th>
                                <th>Cost of Waiting (1 pick)</th>
                                <th>Cost of Waiting (5 picks)</th>
                                <th>Cost of Waiting (10 picks)</th>
                                <th>Cost of Waiting (20 picks)</th>
                            </tr>
                        </thead>
                        <tbody>
                            {Object.entries(r2Data).map(([position, data]) => (
                                <tr key={position}>
                                    <td>{position}</td>
                                    <td>{data.r2}</td>
                                    <td>{data.cost_of_waiting['1_pick']}</td>
                                    <td>{data.cost_of_waiting['5_picks']}</td>
                                    <td>{data.cost_of_waiting['10_picks']}</td>
                                    <td>{data.cost_of_waiting['20_picks']}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default App;
